public class Lingkaran extends Lahan {

    double jari = 7;

    @Override
    double keliling() {
        // TODO Auto-generated method stub
        return 2 * 3.14 * jari;
    }

    @Override
    double luas() {
        // TODO Auto-generated method stub
        return 3.14 * jari * jari;
    }

}
